:root {
  --radius: 0.75rem;

  /* Base ‚Äî warm off-white */
  --background: oklch(0.978 0.004 260);
  --foreground: oklch(0.18 0.025 260);

  /* Cards ‚Äî pure white with subtle shadow */
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.025 260);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.025 260);

  /* Primary ‚Äî deep indigo */
  --primary: oklch(0.44 0.19 264);
  --primary-foreground: oklch(0.99 0 0);

  /* Secondary */
  --secondary: oklch(0.94 0.01 264);
  --secondary-foreground: oklch(0.44 0.19 264);

  /* Muted */
  --muted: oklch(0.945 0.008 260);
  --muted-foreground: oklch(0.52 0.02 260);

  /* Accent ‚Äî warm amber */
  --accent: oklch(0.93 0.04 80);
  --accent-foreground: oklch(0.35 0.1 60);

  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.01 260);
  --input: oklch(0.93 0.008 260);
  --ring: oklch(0.44 0.19 264);

  /* Charts */
  --chart-1: oklch(0.44 0.19 264);
  --chart-2: oklch(0.62 0.18 200);
  --chart-3: oklch(0.72 0.17 80);
  --chart-4: oklch(0.55 0.2 310);
  --chart-5: oklch(0.65 0.19 145);

  /* Sidebar ‚Äî deep indigo */
  --sidebar: oklch(0.26 0.06 264);
  --sidebar-foreground: oklch(0.93 0.01 264);
  --sidebar-primary: oklch(0.62 0.2 264);
  --sidebar-primary-foreground: oklch(0.99 0 0);
  --sidebar-accent: oklch(0.32 0.07 264);
  --sidebar-accent-foreground: oklch(0.93 0.01 264);
  --sidebar-border: oklch(0.35 0.07 264);
  --sidebar-ring: oklch(0.62 0.2 264);

  /* Custom tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.44 0.19 264), oklch(0.38 0.2 280));
  --gradient-card-top: linear-gradient(160deg, oklch(0.48 0.19 264 / 0.08), oklch(0.44 0.19 264 / 0.02));
  --gradient-amber: linear-gradient(135deg, oklch(0.78 0.17 75), oklch(0.72 0.19 60));
  --shadow-card: 0 1px 3px oklch(0.18 0.025 260 / 0.08), 0 4px 16px oklch(0.18 0.025 260 / 0.06);
  --shadow-hero: 0 8px 32px oklch(0.44 0.19 264 / 0.3);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
  --font-sans: 'Plus Jakarta Sans', sans-serif;
}

@layer base {
  body {
    font-family: var(--font-sans);
  }
}

