import { useEffect, useState } from 'react';
import { LivingAppsService } from '@/services/livingAppsService';
import type { Kurse, Anmeldungen } from '@/types/app';
import { BookOpen, Users, GraduationCap, DoorOpen, ClipboardList, TrendingUp, Euro, CheckCircle2, Clock, ArrowRight } from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from 'recharts';
import { format, parseISO, isAfter, isBefore, startOfToday } from 'date-fns';
import { de } from 'date-fns/locale';
import { useNavigate } from 'react-router-dom';

interface Stats {
  dozenten: number;
  teilnehmer: number;
  raeume: number;
  kurse: number;
  anmeldungen: number;
  bezahlt: number;
  unbezahlt: number;
  aktiveKurse: number;
  upcomingKurse: Kurse[];
  recentAnmeldungen: Anmeldungen[];
  umsatz: number;
}

const COLORS = [
  'oklch(0.44 0.19 264)',
  'oklch(0.62 0.18 200)',
  'oklch(0.72 0.17 80)',
  'oklch(0.55 0.2 310)',
  'oklch(0.65 0.19 145)',
];

export default function DashboardOverview() {
  const [stats, setStats] = useState<Stats | null>(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function load() {
      try {
        const [dozenten, teilnehmer, raeume, kurse, anmeldungen] = await Promise.all([
          LivingAppsService.getDozenten(),
          LivingAppsService.getTeilnehmer(),
          LivingAppsService.getRaeume(),
          LivingAppsService.getKurse(),
          LivingAppsService.getAnmeldungen(),
        ]);
        const today = startOfToday();
        const aktiveKurse = kurse.filter(k => {
          const start = k.fields.startdatum ? parseISO(k.fields.startdatum) : null;
          const end = k.fields.enddatum ? parseISO(k.fields.enddatum) : null;
          if (!start || !end) return false;
          return isBefore(start, today) && isAfter(end, today);
        });
        const upcoming = kurse
          .filter(k => k.fields.startdatum && isAfter(parseISO(k.fields.startdatum), today))
          .sort((a, b) => (a.fields.startdatum ?? '').localeCompare(b.fields.startdatum ?? ''))
          .slice(0, 5);
        const bezahlt = anmeldungen.filter(a => a.fields.bezahlt === true).length;
        const unbezahlt = anmeldungen.filter(a => a.fields.bezahlt !== true).length;
        const umsatz = kurse.reduce((sum, k) => sum + (k.fields.preis ?? 0), 0);
        const recent = [...anmeldungen]
          .sort((a, b) => b.createdat.localeCompare(a.createdat))
          .slice(0, 5);

        setStats({
          dozenten: dozenten.length,
          teilnehmer: teilnehmer.length,
          raeume: raeume.length,
          kurse: kurse.length,
          anmeldungen: anmeldungen.length,
          bezahlt,
          unbezahlt,
          aktiveKurse: aktiveKurse.length,
          upcomingKurse: upcoming,
          recentAnmeldungen: recent,
          umsatz,
        });
      } catch (e) {
        console.error(e);
      } finally {
        setLoading(false);
      }
    }
    load();
  }, []);

  const kpiData = stats ? [
    { label: 'Dozenten', value: stats.dozenten, icon: GraduationCap, color: '#7c3aed', bg: 'bg-violet-50', href: '/dozenten' },
    { label: 'Teilnehmer', value: stats.teilnehmer, icon: Users, color: '#0891b2', bg: 'bg-cyan-50', href: '/teilnehmer' },
    { label: 'R√§ume', value: stats.raeume, icon: DoorOpen, color: '#059669', bg: 'bg-emerald-50', href: '/raeume' },
    { label: 'Kurse', value: stats.kurse, icon: BookOpen, color: '#d97706', bg: 'bg-amber-50', href: '/kurse' },
    { label: 'Anmeldungen', value: stats.anmeldungen, icon: ClipboardList, color: '#db2777', bg: 'bg-pink-50', href: '/anmeldungen' },
  ] : [];

  const chartData = stats ? [
    { name: 'Dozenten', wert: stats.dozenten },
    { name: 'Teilnehmer', wert: stats.teilnehmer },
    { name: 'R√§ume', wert: stats.raeume },
    { name: 'Kurse', wert: stats.kurse },
    { name: 'Anmeldungen', wert: stats.anmeldungen },
  ] : [];

  const paymentData = stats ? [
    { name: 'Bezahlt', wert: stats.bezahlt },
    { name: 'Ausstehend', wert: stats.unbezahlt },
  ] : [];

  return (
    <div className="space-y-8">
      {/* Hero */}
      <div
        className="rounded-2xl p-8 text-white relative overflow-hidden"
        style={{ background: 'var(--gradient-hero)', boxShadow: 'var(--shadow-hero)' }}
      >
        <div className="absolute inset-0 opacity-10"
          style={{ backgroundImage: 'radial-gradient(circle at 80% 50%, white 0%, transparent 60%)' }}
        />
        <div className="relative z-10 flex flex-col md:flex-row md:items-center md:justify-between gap-6">
          <div>
            <p className="text-white/70 text-sm font-medium uppercase tracking-widest mb-1">Kursverwaltung</p>
            <h1 className="text-3xl md:text-4xl font-extrabold tracking-tight">
              Willkommen zur√ºck
            </h1>
            <p className="text-white/70 mt-2 text-base max-w-md">
              Verwalten Sie Kurse, Dozenten, Teilnehmer und R√§ume an einem Ort.
            </p>
          </div>
          <div className="flex gap-4 flex-wrap">
            <div className="rounded-xl bg-white/10 backdrop-blur px-5 py-3 flex items-center gap-3 border border-white/20">
              <TrendingUp size={20} className="text-white/80" />
              <div>
                <p className="text-white/60 text-xs">Aktive Kurse</p>
                <p className="text-white font-bold text-xl">{loading ? '‚Äî' : (stats?.aktiveKurse ?? 0)}</p>
              </div>
            </div>
            <div className="rounded-xl bg-white/10 backdrop-blur px-5 py-3 flex items-center gap-3 border border-white/20">
              <Euro size={20} className="text-white/80" />
              <div>
                <p className="text-white/60 text-xs">Gesamtumsatz</p>
                <p className="text-white font-bold text-xl">{loading ? '‚Äî' : `${(stats?.umsatz ?? 0).toLocaleString('de-DE')} ‚Ç¨`}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* KPI Grid */}
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-4">
        {loading
          ? Array.from({ length: 5 }).map((_, i) => (
            <div key={i} className="rounded-xl bg-card border border-border h-28 animate-pulse" />
          ))
          : kpiData.map(kpi => (
            <button
              key={kpi.label}
              onClick={() => navigate(kpi.href)}
              className="group rounded-xl bg-card border border-border p-5 text-left transition-all hover:shadow-md hover:-translate-y-0.5 cursor-pointer"
              style={{ boxShadow: 'var(--shadow-card)' }}
            >
              <div className="flex items-start justify-between mb-3">
                <div className="w-9 h-9 rounded-lg flex items-center justify-center" style={{ backgroundColor: kpi.color + '18' }}>
                  <kpi.icon size={17} style={{ color: kpi.color }} />
                </div>
                <ArrowRight size={14} className="text-muted-foreground opacity-0 group-hover:opacity-100 transition-opacity mt-1" />
              </div>
              <p className="text-2xl font-bold text-foreground">{kpi.value}</p>
              <p className="text-xs text-muted-foreground font-medium mt-0.5">{kpi.label}</p>
            </button>
          ))
        }
      </div>

      {/* Charts + Lists Row */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Bar Chart */}
        <div className="lg:col-span-2 rounded-xl bg-card border border-border p-6" style={{ boxShadow: 'var(--shadow-card)' }}>
          <h2 className="text-base font-semibold text-foreground mb-1">√úbersicht</h2>
          <p className="text-xs text-muted-foreground mb-5">Anzahl Eintr√§ge je Kategorie</p>
          {loading ? (
            <div className="h-48 animate-pulse rounded-lg bg-muted" />
          ) : (
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={chartData} barSize={32}>
                <XAxis dataKey="name" tick={{ fontSize: 12, fill: 'oklch(0.52 0.02 260)' }} axisLine={false} tickLine={false} />
                <YAxis allowDecimals={false} tick={{ fontSize: 12, fill: 'oklch(0.52 0.02 260)' }} axisLine={false} tickLine={false} />
                <Tooltip
                  contentStyle={{ borderRadius: '10px', border: 'none', boxShadow: '0 4px 20px rgba(0,0,0,0.12)', fontSize: 13 }}
                  cursor={{ fill: 'oklch(0.44 0.19 264 / 0.06)' }}
                />
                <Bar dataKey="wert" radius={[6, 6, 0, 0]}>
                  {chartData.map((_, i) => (
                    <Cell key={i} fill={COLORS[i % COLORS.length]} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          )}
        </div>

        {/* Payment Status */}
        <div className="rounded-xl bg-card border border-border p-6" style={{ boxShadow: 'var(--shadow-card)' }}>
          <h2 className="text-base font-semibold text-foreground mb-1">Zahlungsstatus</h2>
          <p className="text-xs text-muted-foreground mb-5">Anmeldungen nach Bezahlung</p>
          {loading ? (
            <div className="space-y-3">
              <div className="h-16 animate-pulse rounded-lg bg-muted" />
              <div className="h-16 animate-pulse rounded-lg bg-muted" />
            </div>
          ) : (
            <div className="space-y-3">
              <div className="rounded-xl p-4 bg-emerald-50 border border-emerald-100">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <CheckCircle2 size={18} className="text-emerald-600" />
                    <span className="text-sm font-medium text-emerald-800">Bezahlt</span>
                  </div>
                  <span className="text-2xl font-bold text-emerald-700">{stats?.bezahlt ?? 0}</span>
                </div>
                {(stats?.anmeldungen ?? 0) > 0 && (
                  <div className="mt-2 h-1.5 rounded-full bg-emerald-200 overflow-hidden">
                    <div
                      className="h-full rounded-full bg-emerald-500 transition-all"
                      style={{ width: `${Math.round(((stats?.bezahlt ?? 0) / (stats?.anmeldungen ?? 1)) * 100)}%` }}
                    />
                  </div>
                )}
              </div>
              <div className="rounded-xl p-4 bg-amber-50 border border-amber-100">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <Clock size={18} className="text-amber-600" />
                    <span className="text-sm font-medium text-amber-800">Ausstehend</span>
                  </div>
                  <span className="text-2xl font-bold text-amber-700">{stats?.unbezahlt ?? 0}</span>
                </div>
                {(stats?.anmeldungen ?? 0) > 0 && (
                  <div className="mt-2 h-1.5 rounded-full bg-amber-200 overflow-hidden">
                    <div
                      className="h-full rounded-full bg-amber-500 transition-all"
                      style={{ width: `${Math.round(((stats?.unbezahlt ?? 0) / (stats?.anmeldungen ?? 1)) * 100)}%` }}
                    />
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Upcoming Courses */}
      {!loading && (stats?.upcomingKurse.length ?? 0) > 0 && (
        <div className="rounded-xl bg-card border border-border p-6" style={{ boxShadow: 'var(--shadow-card)' }}>
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-base font-semibold text-foreground">Kommende Kurse</h2>
              <p className="text-xs text-muted-foreground">N√§chste geplante Kurse</p>
            </div>
            <button
              onClick={() => navigate('/kurse')}
              className="text-xs text-primary font-medium flex items-center gap-1 hover:underline"
            >
              Alle anzeigen <ArrowRight size={12} />
            </button>
          </div>
          <div className="space-y-2">
            {stats!.upcomingKurse.map(kurs => (
              <div
                key={kurs.record_id}
                className="flex items-center justify-between rounded-lg px-4 py-3 bg-muted/50 hover:bg-muted transition-colors"
              >
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 rounded-lg bg-primary/10 flex items-center justify-center">
                    <BookOpen size={14} className="text-primary" />
                  </div>
                  <div>
                    <p className="text-sm font-semibold text-foreground">{kurs.fields.titel ?? '‚Äî'}</p>
                    <p className="text-xs text-muted-foreground">
                      {kurs.fields.startdatum
                        ? format(parseISO(kurs.fields.startdatum), 'dd. MMM yyyy', { locale: de })
                        : 'Kein Datum'}
                    </p>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  {kurs.fields.preis != null && (
                    <span className="text-sm font-bold text-foreground">
                      {kurs.fields.preis.toLocaleString('de-DE')} ‚Ç¨
                    </span>
                  )}
                  {kurs.fields.max_teilnehmer != null && (
                    <span className="text-xs text-muted-foreground bg-border px-2 py-0.5 rounded-full">
                      max. {kurs.fields.max_teilnehmer}
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

