          <div className="w-14 h-14 rounded-2xl flex items-center justify-center shrink-0" style={{ background: 'oklch(0.62 0.15 148 / 0.12)' }}>
            <Euro size={22} style={{ color: 'oklch(0.52 0.15 148)' }} />
